pub struct HSKLists<'a> {
    hsk1: [&'a str; 150],
    hsk2: [&'a str; 151],
    hsk3: [&'a str; 300],
    hsk4: [&'a str; 600],
    hsk5: [&'a str; 1300],
    hsk6: [&'a str; 2500],
}

pub type Hsk = u8;

impl HSKLists<'_> {
    pub fn level(&self, query: &str) -> Option<Hsk> {
        let hsks: [&[&str]; 6] = [
            &self.hsk1, &self.hsk2, &self.hsk3, &self.hsk4, &self.hsk5, &self.hsk6,
        ];
        hsks.iter()
            .enumerate()
            .find(|(_, els)| els.contains(&query))
            .map(|(level, _)| (level as Hsk) + 1)
    }
}

